// Approved senders list â€” replace with DataTable or API lookup in production
const APPROVED_SENDERS = [
  {
    email: 'wladisha@gmail.com',
    name: 'Wladisha (Test)',
    authorizedPharmacyIds: ['PH001', 'PH002'],
  },
  {
    email: 'test@medika.hr',
    name: 'Test Sender',
    authorizedPharmacyIds: ['PH001'],
  },
];

const input = $input.first().json;
const senderEmail = (input.senderEmail || '').toLowerCase().trim();

// Exact email match
const sender = APPROVED_SENDERS.find(
  s => s.email.toLowerCase() === senderEmail
);

if (sender) {
  return [{
    json: {
      valid: true,
      senderEmail: senderEmail,
      senderName: sender.name,
      authorizedPharmacyIds: sender.authorizedPharmacyIds,
      validatedAt: new Date().toISOString(),
    }
  }];
}

// Domain match fallback (e.g. anyone @medika.hr is valid)
const domain = senderEmail.split('@')[1];
const APPROVED_DOMAINS = ['medika.hr'];

if (APPROVED_DOMAINS.includes(domain)) {
  return [{
    json: {
      valid: true,
      senderEmail: senderEmail,
      senderName: senderEmail,
      authorizedPharmacyIds: ['ALL'],
      validatedAt: new Date().toISOString(),
    }
  }];
}

// Not authorized
return [{
  json: {
    valid: false,
    senderEmail: senderEmail,
    reason: `Sender ${senderEmail} is not in the approved senders list`,
    validatedAt: new Date().toISOString(),
  }
}];
