// Build HTML summary for approval email
const input = $input.first().json;
const lines = input.validatedLines || [];
const summary = input.summary || { valid: 0, invalid: 0, warnings: 0 };
const senderEmail = input.senderEmail || 'unknown';
const emailSubject = input.emailSubject || 'N/A';

let html = `<h2>Pre-Order Approval Required</h2>`;
html += `<p><strong>From:</strong> ${senderEmail}<br>`;
html += `<strong>Subject:</strong> ${emailSubject}<br>`;
html += `<strong>Lines:</strong> ${lines.length} | `;
html += `<strong>Valid:</strong> ${summary.valid} | `;
html += `<strong>Invalid:</strong> ${summary.invalid} | `;
html += `<strong>With Warnings:</strong> ${summary.warnings}</p>`;

html += `<table border="1" cellpadding="5" cellspacing="0" style="border-collapse:collapse;font-size:13px;">`;
html += `<tr style="background:#f0f0f0;"><th>#</th><th>Article ID</th><th>Article Name</th><th>Qty</th><th>Unit</th><th>Customer</th><th>Status</th><th>Method</th></tr>`;

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const bgColor = !line.valid ? '#ffe0e0' :
                  line.warnings && line.warnings.length > 0 ? '#fff3cd' :
                  line.parseMethod === 'AI_EXTRACTED' ? '#e0e7ff' : '#e0ffe0';
  const status = !line.valid ? '\u2717 ' + (line.errors || []).join(', ') :
                 line.warnings && line.warnings.length > 0 ? '\u26a0 ' + line.warnings.join(', ') : '\u2713 Valid';

  html += `<tr style="background:${bgColor};">`;
  html += `<td>${i + 1}</td>`;
  html += `<td>${line.articleId || line.drugCode || '-'}</td>`;
  html += `<td>${line.articleName || '-'}</td>`;
  html += `<td>${line.quantity}</td>`;
  html += `<td>${line.unit || 'kom'}</td>`;
  html += `<td>${line.customerName || line.customer || '-'}</td>`;
  html += `<td>${status}</td>`;
  html += `<td>${line.parseMethod || '-'}</td>`;
  html += `</tr>`;
}
html += `</table>`;

if (summary.invalid > 0) {
  html += `<p style="color:red;"><strong>Warning:</strong> ${summary.invalid} line(s) have validation errors and will be skipped.</p>`;
}

const aiLines = lines.filter(l => l.parseMethod === 'AI_EXTRACTED');
if (aiLines.length > 0) {
  html += `<p style="color:#3b5998;"><strong>Note:</strong> ${aiLines.length} line(s) were extracted by AI and should be reviewed carefully (highlighted in blue).</p>`;
}

return [{
  json: {
    ...input,
    approvalHtml: html
  }
}];

// Combine the approval decision with the original order data
const formData = $input.first().json;
const decision = (formData.Decision || formData.decision || '').trim();
const comments = formData.Comments || formData.comments || '';

// Get order data from the Build Approval Summary node
const orderData = $('Build Approval Summary').first().json;

return [{
  json: {
    decision: decision,
    comments: comments,
    approved: decision === 'Approve',
    rejected: decision === 'Reject',
    changesRequested: decision === 'Request Changes',
    decidedAt: new Date().toISOString(),
    config: orderData.config,
    validatedLines: orderData.validatedLines,
    summary: orderData.summary,
    senderEmail: orderData.senderEmail,
    emailSubject: orderData.emailSubject,
    emailId: orderData.emailId,
    receivedAt: orderData.receivedAt,
    customer: orderData.customer,
    deliveryPlace: orderData.deliveryPlace,
    businessCenter: orderData.businessCenter,
    lineCount: orderData.lineCount
  }
}];
